import React from 'react';
import PropTypes from 'prop-types';

const HiloList = ({ hilos, onSelectHilo, onDeleteHilo }) => {
  return (
    <ul className="hilo-list"> {/* Clase para la lista de hilos */}
      {hilos.map((hilo) => (
        <li key={hilo.id} className="hilo-item"> {/* Clase para cada hilo */}
          <h3>{hilo.titulo}</h3>
          <p>{hilo.contenido}</p>
          <button onClick={() => onSelectHilo(hilo)}>Ver Hilo</button>
          <button className="eliminar-hilo-btn" onClick={() => onDeleteHilo(hilo.id)}>Eliminar</button>
        </li>
      ))}
    </ul>
  );
};

HiloList.propTypes = {
  hilos: PropTypes.array.isRequired,
  onSelectHilo: PropTypes.func.isRequired,
  onDeleteHilo: PropTypes.func.isRequired,
};

export default HiloList;
