import React, { useState, useEffect } from "react";
import Comentario from "./Comentario";

const Hilo = ({ hilo, volver }) => {
  const [comentarios, setComentarios] = useState([]);
  const [nuevoComentario, setNuevoComentario] = useState("");

  // Obtener comentarios del backend al montar el componente
  useEffect(() => {
    fetch(`http://localhost:5000/comentarios?hiloId=${hilo.id}`)
      .then((response) => response.json())
      .then((data) => setComentarios(data))
      .catch((error) => console.error("Error al cargar los comentarios:", error));
  }, [hilo.id]);

  const agregarComentario = () => {
    const nuevoComentarioData = {
      contenido: nuevoComentario,
      autor: "Usuario", // Ajusta el autor según tu lógica
      fecha: new Date().toISOString(),
      hiloId: hilo.id,
    };

    fetch("http://localhost:5000/comentario", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(nuevoComentarioData),
    })
      .then((response) => response.json())
      .then((comentarioAgregado) => {
        setComentarios((prevComentarios) => [...prevComentarios, comentarioAgregado]);
        setNuevoComentario("");
      })
      .catch((error) => console.error("Error al agregar comentario:", error));
  };

  return (
    <div>
      <h2>{hilo.titulo}</h2>
      <p>{hilo.contenido}</p> {/* Aquí se muestra el contenido del hilo */}
      <h3>Comentarios</h3>
      <ul>
        {comentarios.map((comentario) => (
          <Comentario key={comentario.id} comentario={comentario} />
        ))}
      </ul>
      <input
        type="text"
        value={nuevoComentario}
        onChange={(e) => setNuevoComentario(e.target.value)}
        placeholder="Añadir un comentario"
      />
      <button onClick={agregarComentario}>Añadir comentario</button>
      <button onClick={volver}>Volver</button>
    </div>
  );
};

export default Hilo;
