import React from 'react';

const HiloList = ({ hilos, onSelectHilo, setHilos }) => {
  const eliminarHilo = async (id) => {
    try {
      // Realiza la solicitud DELETE al backend
      const response = await fetch(`http://localhost:5000/hilo/${id}`, {
        method: 'DELETE',
      });
      
      if (response.ok) {
        // Filtrar los hilos existentes para eliminar el hilo borrado
        setHilos((prevHilos) => prevHilos.filter((hilo) => hilo.id !== id));
      } else {
        console.error('Error al eliminar el hilo');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Hilos</h2>
      <ul>
        {hilos.map((hilo) => (
          <li key={hilo.id}>
            <h3>{hilo.titulo}</h3>
            <p>{hilo.contenido}</p>
            <button onClick={() => onSelectHilo(hilo)}>Ver Hilo</button>
            <button onClick={() => eliminarHilo(hilo.id)}>Eliminar Hilo</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HiloList;
