import React, { useState, useEffect } from "react";
import './App.css';
import HiloList from "./componentes/HiloList";
import Hilo from "./componentes/Hilo";
import NuevoHilo from "./componentes/NuevoHilo";

const App = () => {
  const [hilos, setHilos] = useState([]); // Estado para los hilos
  const [hiloSeleccionado, setHiloSeleccionado] = useState(null);

  // Cargar los hilos al montar el componente
  useEffect(() => {
    const fetchHilos = async () => {
      const response = await fetch('http://localhost:5000/hilos');
      const data = await response.json();
      setHilos(data);
    };

    fetchHilos();
  }, []);

  const eliminarHilo = async (id) => {
    const response = await fetch(`http://localhost:5000/hilo/${id}`, { method: 'DELETE' });
    if (response.ok) {
      // Filtrar los hilos eliminados
      const nuevosHilos = hilos.filter(hilo => hilo.id !== id);
      setHilos(nuevosHilos); // Actualizar el estado
    } else {
      console.error('Error al eliminar el hilo');
    }
  };
  return (
    <div>
      <div className="barra-superior">
        <h1>Mi Foro</h1>
      </div>
      <div className="main">
        {creandoHilo ? (
          <NuevoHilo setCreandoHilo={setCreandoHilo} setHilos={setHilos} />
        ) : selectedHilo ? (
          <Hilo hilo={selectedHilo} volver={() => setSelectedHilo(null)} />
        ) : (
          <>
            <HiloList hilos={hilos} onSelectHilo={setSelectedHilo} />
            <button onClick={() => setCreandoHilo(true)} className="nuevo-hilo-boton">
              Crear Nuevo Hilo
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default App;
