import React, { useState } from "react";

const NuevoHilo = ({ onCrearHilo, volver }) => {
  const [titulo, setTitulo] = useState("");
  const [contenido, setContenido] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nuevoHilo = {
      id: Date.now(), // o cualquier lógica para generar un ID único
      titulo,
      contenido,
      autor: "René", // Cambia esto según sea necesario
      fecha: new Date().toISOString()
    };

    // Aquí puedes hacer la solicitud para agregar el hilo a la base de datos
    // await fetch('http://localhost:5000/hilo', { method: 'POST', headers: {'Content-Type': 'application/json'}, body: JSON.stringify(nuevoHilo) });

    onCrearHilo(nuevoHilo);
  };

  return (
    <div>
      <h2>Crear Nuevo Hilo</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={titulo} 
          onChange={(e) => setTitulo(e.target.value)} 
          placeholder="Título del hilo" 
          required 
        />
        <textarea 
          value={contenido} 
          onChange={(e) => setContenido(e.target.value)} 
          placeholder="Contenido del hilo" 
          required 
        ></textarea>
        <button type="submit">Crear Hilo</button>
        <button type="button" onClick={volver}>Volver</button>
      </form>
    </div>
  );
};

export default NuevoHilo;
