import React from 'react';

const HiloList = ({ hilos, setHilos, eliminarHilo, onSelectHilo }) => {
  const handleEliminar = async (id) => {
    const response = await fetch(`http://localhost:5000/hilo/${id}`, { method: 'DELETE' });
    if (response.ok) {
      // Filtrar los hilos eliminados
      const nuevosHilos = hilos.filter(hilo => hilo.id !== id);
      setHilos(nuevosHilos); // Actualizar el estado
    } else {
      console.error('Error al eliminar el hilo');
    }
  };

  return (
    <div>
      <h1>Lista de Hilos</h1>
      <ul>
        {hilos.map((hilo) => (
          <li key={hilo.id}>
            <span>{hilo.titulo}</span>
            <button onClick={() => onSelectHilo(hilo)}>Ver</button>
            <button onClick={() => handleEliminar(hilo.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HiloList;
