import React from "react";

const HiloList = ({ hilos, onSelectHilo }) => {
  if (!hilos || hilos.length === 0) {
    return <p>No hay hilos disponibles.</p>; // Manejo cuando no hay hilos
  }

  return (
    <div>
      {hilos.map((hilo) => (
        <div key={hilo.id} className="fila">
          <div className="fondo">
            <span className="fecha">{new Date(hilo.fecha).toLocaleString()}</span>
            <button className="hilo-boton" onClick={() => onSelectHilo(hilo)}>
              {hilo.titulo}
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};


export default HiloList;
