import React, { useState } from "react";
import './App.css'; 
import HiloList from "./componentes/HiloList";
import Hilo from "./componentes/Hilo";
import NuevoHilo from "./componentes/NuevoHilo"; // Asegúrate de importar el nuevo componente

const App = () => {
  const [hilos, setHilos] = useState([]); // Inicialmente vacío para eliminar los hilos existentes
  const [selectedHilo, setSelectedHilo] = useState(null);
  const [creandoHilo, setCreandoHilo] = useState(false); // Estado para manejar la creación de hilos

  const crearNuevoHilo = (nuevoHilo) => {
    setHilos([...hilos, nuevoHilo]);
    setCreandoHilo(false);
  };

  return (
    <div>
      <div className="barra-superior">
        <h1>Mi Foro</h1>
      </div>
      <div className="main">
        {creandoHilo ? (
          <NuevoHilo onCrearHilo={crearNuevoHilo} volver={() => setCreandoHilo(false)} />
        ) : selectedHilo ? (
          <Hilo hilo={selectedHilo} volver={() => setSelectedHilo(null)} />
        ) : (
          <>
            <button onClick={() => setCreandoHilo(true)}>Crear Nuevo Hilo</button>
            <HiloList hilos={hilos} onSelectHilo={setSelectedHilo} />
          </>
        )}
      </div>
    </div>
  );
};

export default App;
